mapper.select:
SELECT COUNT(member_id)
FROM member
WHERE member_id = #{userid}

SELECT COUNT(member_nickname)
FROM member
WHERE member_nickname = #{nickname}

mapper.update:
UPDATE member
SET is_active = '0'
WHERE member_id = #{member_id}
        AND member_name = #{member_name}
        AND member_nickname = 3{member_nickname}
        AND is_active = '1'
